.vertical-control {
    -webkit-transform: rotate(90deg) translateX(-50%);
    transform: rotate(90deg) translateX(-50%);
    right: initial;
    left: 50%;
}

#cms_merchzonetwo .cms-merchzone {
    position: relative;
    padding-bottom: 25px;

    .cms-merchzone-heading {
        color: $heading-color;
        font-family: $heading-font-family;
        font-size: $heading-font-size;
        font-style: $heading-font-style;
        text-decoration: $heading-text-decoration;
        margin-top: $heading-margin-top;
        margin-bottom: $heading-margin-bottom;
        text-transform: $heading-text-transform;
        padding-bottom: 40px;

        @media (min-width: $screen-md-min) {
            font-size: $heading-desktop-font-size;
        }
    }

    .bx-wrapper{
        .bx-viewport{
            padding-bottom: 0px;
            box-sizing: content-box;
            height: auto !important;
        }
    }

    .cms-merchzone-item {
        text-align: center;
        cursor: pointer;
        border: 1px solid #cbcbcb;
        padding: 20px;
        border-radius: 15px;

        .item-image-wrapper {
            padding: $image-padding;
            display: flex;
            flex-direction: column;
            justify-content: center;
        }
        .item-name{
            min-height: 46px;
            height: 100%;
            display: -webkit-box;
            line-clamp: 2;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
            text-overflow: ellipsis;
            margin-bottom: 10px;
            font-size: 1rem;
            font-weight: 600;
            text-transform: uppercase;
        }
        .item-price{
            margin-bottom: 10px;
        }
    }

    .cms-merchzone-grid {
        margin-left: 0;

        & > li {
            list-style: none;
            display: inline-block;

            .item-image {
                width: 100%;
            }
        }
    }

    .cms-merchzone-slider {
        margin-left: 0;
        li{
            padding: 0 15px;
        }

        &-next {
            font-size: 30px;
            position: absolute;
            right: -10px;
            top: 51.5%;
            transform: translateY(-50%);

            &.cms-merchzone-vertical-control {
                @extend .vertical-control;
                top: initial;
                bottom: -10px;
                left: 48.5%;
            }
        }

        &-prev {
            font-size: 30px;
            position: absolute;
            top: 51.5%;
            left: -10px;
            transform: translateY(-50%);

            &.cms-merchzone-vertical-control {
                @extend .vertical-control;
                top: 22px;
                bottom: initial;
                left: 48.5%;
            }
        }
    }

    &-see-more {
        display: inline-block;
        color: $button-text-color;
        background-color: $button-background-color;
        border-radius: $button-border-radius;
        border: 1px solid $button-border-color;
        text-transform: $button-text-transform;
        padding: $button-padding-v $button-padding-h;
        font-size: $button-font-size;
        line-height: $button-line-height;
        letter-spacing: $button-letter-spacing;
        width: $button-mobile-width;

        @media (min-width: $screen-sm-min) {
            width: $button-desktop-width;
        }

        &:hover {
            color: $button-hover-text-color;
            background-color: $button-hover-background-color;
            border-color: $button-hover-border-color;
        }

        &:active {
            color: $button-active-text-color;
            background-color: $button-active-background-color;
            border-color: $button-active-border-color;
        }
    }
}
